                do
                {
                    using (ThreadSafeForm<SelectVehicleMod> frmPickVehicleMod
                           = await ThreadSafeForm<SelectVehicleMod>.GetAsync(
                                                                       () => new SelectVehicleMod(CharacterObject,
                                                                           objVehicle), GenericToken)
