                    {
                        using (ThreadSafeForm<SellItem> frmSell
                               = await ThreadSafeForm<SellItem>.GetAsync(() => new SellItem(), GenericToken)
                                                               .ConfigureAwait(false))
                        {
                            if (await frmSell.ShowDialogSafeAsync(this, GenericToken).ConfigureAwait(false)
