                            await tabCyberwareCM.DoThreadSafeAsync(
                                x => x.Visible = objCyberware.SourceType == Improvement.ImprovementSource.Cyberware,
                                token).ConfigureAwait(false);
                            // Buttons
                            await cmdDeleteCyberware.DoThreadSafeAsync(
