                        string strText2 = await objComplexForm.DisplayDurationAsync(GlobalSettings.Language, token)
                                                              .ConfigureAwait(false);
                        await lblDuration.DoThreadSafeAsync(x => x.Text = strText2, token).ConfigureAwait(false);
